import React,{useState}  from 'react'
import Card from './item'
import ModalAdd from "./modalAdd"
import {connect} from 'react-redux' ;
import {getList , deleteItem , addItem , updateItem} from "./redux/actions/actions"

const List =(props)=>{
  

const deleteDish=(id)=>{
   props.deleteItem(id);
   
   console.log("hello delete func !:",id)
}

  const modifyDish=(item)=>{
   props.updateItem(item);
     console.log("item from List Component:",item)
  }

 const  [list,setList]=useState([])

 const addDish=(item)=>{
   setList([...list,item])
   console.log(item) ; 
   
}

  
 return (
   <div>
<ModalAdd list={props.list} addDish={addDish}/>
<div className="myStyle d-flex flex-wrap justify-content-center ">
   {props.list.length===0?<div style={{marginTop:20,fontSize:16}}>No items !</div>:props.list.map((element,index)=><Card key={element.id} item={element} deleteDish={deleteDish}  modifyDish={modifyDish}/>)}
   </div> 
   </div>
   )
}


const mapStateToProps=(state)=>{
   return{
      list : state.items
   }
}
const NewList = connect(mapStateToProps,{getList, deleteItem, updateItem})(List)

export default NewList
