import React,{useState}  from 'react'
import Card from './item'
import ModalAdd from "./modalAdd"

const arr=[
    {
       id:0,
       dish:"Couscous",
       image:"couscous.jpg",
       price:"20000 dt"
    },
    {   id:1,
        dish:"Pizza",
        image:"pizza.jpg",
        price:"15000 dt"
     },
     {  id:2,
        dish:"Mixed grill",
        image:"mixed-grill.jpeg",
        price:"20000 dt"
     },
     {
        id:3,
        dish:"Seafood ",
        image:"seafood.jpg",
        price:"45000 dt"
     },
    ]



const List =(props)=>{
  

const deleteDish=(id)=>{
   setList(list.filter((el)=>el.id!==id))
   console.log("hello delete func !:",id)
}

  const modifyDish=(item)=>{

   setList(list.map((element,index)=>element.id===item.id?element=item:element))
     console.log("item from List Component:",item)
  }

    const  [list,setList]=useState(arr)

const addDish=(item)=>{
   setList([...list,item])
   console.log(item) ; 
}

  
 return (
   <div>
<ModalAdd list={list} addDish={addDish}/>
<div className="myStyle d-flex flex-wrap justify-content-center ">
   {list.length===0?<div style={{marginTop:20,fontSize:16}}>No items !</div>:list.map((element,index)=><Card key={element.id} item={element} deleteDish={deleteDish}  modifyDish={modifyDish}/>)}
   </div> 
   </div>
   )
}
export default List
